LinuxWindows <pre><code>#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;
#include &lt;string.h&gt;
#include &lt;unistd.h&gt;
#include &lt;sys/socket.h&gt;
#include &lt;sys/types.h&gt;
#include &lt;arpa/inet.h&gt;

int main(int argc, char *argv[]) {
  if (2 != argc) {
    fprintf(stderr, "Usage: %s &lt;port&gt;\n", argv[0]);
    exit(EXIT_FAILURE);
  }

  // 1. ​创建​套​接字​
  int sockfd = socket(AF_INET, SOCK_STREAM, 0);
  if (-1 == sockfd) {
    perror("socket() error");
    exit(EXIT_FAILURE);
  }

  // 2. ​绑定​地址​信息​
  struct sockaddr_in serv_addr;
  memset(&amp;serv_addr, 0, sizeof(serv_addr));
  serv_addr.sin_family = AF_INET;
  serv_addr.sin_addr.s_addr = htonl(INADDR_ANY);
  serv_addr.sin_port = htons(atoi(argv[1]));
  if (-1 == bind(sockfd, (struct sockaddr *)&amp;serv_addr, sizeof(serv_addr))) {
    perror("bind() error");
    close(sockfd);
    exit(EXIT_FAILURE);
  }

  // 3. ​打开​可​连接​状态​，​进入​监听​
  if (-1 == listen(sockfd, 5)) {
    perror("listen() error");
    close(sockfd);
    exit(EXIT_FAILURE);
  }

  struct sockaddr_in clnt_addr;
  memset(&amp;clnt_addr, 0, sizeof(clnt_addr));
  socklen_t clnt_len = sizeof(clnt_addr);
  for (int i = 0; i &lt; 5; ++i) {
    int clntfd = accept(sockfd, (struct sockaddr *)&amp;clnt_addr, &amp;clnt_len);
    if (-1 == clntfd) {
      perror("accept() error");
      close(sockfd);
      exit(EXIT_FAILURE);
    } else {
      printf("Connected clinet: %d\n", clntfd);
    }

    char message[1024] = {0};
    int str_len = 0;
    while (0 != (str_len = read(clntfd, message, 1024)))
      write(clntfd, message, str_len);

    close(clntfd);
  }
  close(sockfd);

  return 0;
}
</code></pre> <pre><code>#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;
#include &lt;string.h&gt;
#include &lt;WinSock2.h&gt;

void error_handling(const char *msg);

int main(int argc, char *argv[]) {
  if (2 != argc) {
    fprintf(stderr, "Usage: %s &lt;port&gt;\n", argv[0]);
    exit(EXIT_FAILURE);
  }

  // ​初始化​ winsock ​库​
  WSADATA wsa_data;
  WSAStartup(MAKEWORD(2, 2), &amp;wsa_data);

  // 1. ​创建​套​接字​
  SOCKET serv_sock = socket(AF_INET, SOCK_STREAM, 0);
  if (-1 == serv_sock)
    error_handling("socket error");

  // 2. ​绑定​本地​信息​
  struct sockaddr_in serv_addr;
  memset(&amp;serv_addr, 0, sizeof(serv_addr));
  serv_addr.sin_family = AF_INET;
  serv_addr.sin_addr.s_addr = htonl(INADDR_ANY);
  serv_addr.sin_port = htons(atoi(argv[1]));
  if (-1 == bind(serv_sock, (struct sockaddr *)&amp;serv_addr, sizeof(serv_addr))) {
    closesocket(serv_sock);
    error_handling("bind error");
  }

  // 3. ​打开​可​连接​状态​
  if (-1 == listen(serv_sock, 5)) {
    closesocket(serv_sock);
    error_handling("listen error");
  }

  // 4. ​接收​客户端​的​连接​
  struct sockaddr_in clnt_addr;
  int clnt_len = sizeof(clnt_addr);
  SOCKET clnt_sock = accept(serv_sock, (struct sockaddr *)&amp;clnt_addr, &amp;clnt_len );
  if (-1 == clnt_sock) {
    closesocket(serv_sock);
    error_handling("accept error");
  }
  char message[] = "hello world!";
  int size = send(clnt_sock, message, sizeof(message), 0);
  if (-1 == size) {
    closesocket(serv_sock);
    error_handling("send error");
  }

  closesocket(clnt_sock);
  closesocket(serv_sock);
  // ​注销​ winsock ​库​
  WSACleanup();
  return 0;
}

void error_handling(const char *msg) {
  fputs(msg, stderr);
  fputc('\n', stderr);
  exit(EXIT_FAILURE);
}
</code></pre>

LinuxWindows <pre><code>#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;
#include &lt;string.h&gt;
#include &lt;unistd.h&gt;
#include &lt;sys/socket.h&gt;
#include &lt;sys/types.h&gt;
#include &lt;arpa/inet.h&gt;

int main(int argc, char *argv[]) {
  if (3 != argc) {
    fprintf(stderr, "Usage: %s &lt;ip&gt; &lt;port&gt;\n", argv[0]);
    exit(EXIT_FAILURE);
  }

  // 1. ​创建​套​接字​
  int clntfd = socket(AF_INET, SOCK_STREAM, 0);
  if (-1 == clntfd) {
    perror("socket() error");
    exit(EXIT_FAILURE);
  }

  // 2. ​发送​连接​请求​
  struct sockaddr_in clnt_addr;
  memset(&amp;clnt_addr, 0, sizeof(clnt_addr));
  clnt_addr.sin_family = AF_INET;
  clnt_addr.sin_addr.s_addr = inet_addr(argv[1]);
  clnt_addr.sin_port = htons(atoi(argv[2]));
  if (-1 == connect(clntfd, (struct sockaddr *)&amp;clnt_addr, sizeof(clnt_addr))) {
    perror("connect() error");
    close(clntfd);
    exit(EXIT_FAILURE);
  } else {
    printf("Connected ......\n");
  }

  while (1) {
    printf("Input message(Q/q to quit): ");
    char message[1024] = {0};
    fgets(message, 1024, stdin);
    if (!strcmp(message, "q\n") || !strcmp(message, "Q\n"))
      break;

    int len = write(clntfd, message, sizeof(message));
    // ​由于​服务器端​是​循环​发送​，​如果​客户端​一次​接收​，​可能​接收​的​不是​完整​信息​，​因此​也​建议​循环​读取​
    int recv_len = 0;
    while (recv_len &lt; len) {
      int rlen = read(clntfd, message, 1024);
      if (-1 == rlen) {
        perror("read() error");
        close(clntfd);
        exit(EXIT_FAILURE);
      }

      recv_len += rlen;
    }
    printf("read message from server: %s\n", message);
  }
  close(clntfd);

  return 0;
}
</code></pre> <pre><code>#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;
